import React, { useState, useEffect } from 'react';
import { PieChart, Pie, Cell, Legend } from 'recharts';
import './App.css';
import {
  registerUser,
  loginUser,
  fetchShoppingLists,
  createShoppingList,
  deleteShoppingList,
  archiveShoppingList,
  addParticipantByEmail,
  addItemToList,
} from './services/apiService';

const App = () => {
  const [shoppingLists, setShoppingLists] = useState([]);
  const [newListName, setNewListName] = useState('');
  const [newParticipantEmail, setNewParticipantEmail] = useState('');
  const [newItemName, setNewItemName] = useState('');
  const [selectedListId, setSelectedListId] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));
  const [authMode, setAuthMode] = useState('login');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');
  const [authError, setAuthError] = useState('');
  const COLORS = ['#0088FE', '#FF8042'];

  // Загружаем списки после аутентификации
  useEffect(() => {
    if (isAuthenticated) {
      fetchShoppingLists()
        .then((lists) => setShoppingLists(lists))
        .catch((err) => console.error('Error fetching shopping lists:', err));
    }
  }, [isAuthenticated]);

  const handleAuthSubmit = async () => {
    try {
      setAuthError('');
      if (authMode === 'register') {
        await registerUser({ email, password, username });
        setAuthMode('login');
      } else {
        const response = await loginUser({ email, password });
        localStorage.setItem('token', response.token);
        setIsAuthenticated(true);
      }
    } catch (err) {
      setAuthError(err.response?.data?.message || 'Authentication failed');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    setEmail('');
    setPassword('');
  };

  const handleAddNewList = async () => {
    if (!newListName.trim()) {
      console.error('List name is empty');
      return;
    }

    try {
      const newList = await createShoppingList(newListName);
      console.log('New list added:', newList); // Логируем созданный список
      setShoppingLists([...shoppingLists, newList]); // Добавляем список в состояние
      setNewListName(''); // Очищаем поле ввода
      setSelectedListId(newList._id); // Устанавливаем ID как выбранный
      console.log('New list selected:', newList._id); // Логируем выбранный ID
    } catch (err) {
      console.error('Failed to add new list:', err);
    }
  };


  const handleDeleteList = async (listId) => {
    try {
      await deleteShoppingList(listId);
      setShoppingLists(shoppingLists.filter((list) => list.id !== listId));
      if (listId === selectedListId) {
        setSelectedListId(null);
      }
    } catch (err) {
      console.error('Failed to delete list:', err);
    }
  };

  const handleArchiveList = async (listId) => {
    try {
      await archiveShoppingList(listId);
      const updatedLists = await fetchShoppingLists();
      setShoppingLists(updatedLists);
    } catch (err) {
      console.error('Failed to archive list:', err);
    }
  };

  const handleAddParticipant = async () => {
    if (!newParticipantEmail.trim() || selectedListId === null) return;
    try {
      const updatedList = await addParticipantByEmail(selectedListId, newParticipantEmail);
      setShoppingLists(
        shoppingLists.map((list) =>
          list.id === selectedListId ? { ...list, ...updatedList } : list
        )
      );
      setNewParticipantEmail('');
    } catch (err) {
      console.error('Failed to add participant:', err);
    }
  };

  const handleAddItem = async () => {
    if (!newItemName.trim()) return;
    try {
      const updatedList = await addItemToList(selectedListId, newItemName);
      setShoppingLists((prevLists) =>
        prevLists.map((list) =>
          list._id === selectedListId ? { ...list, items: updatedList.items } : list
        )
      );
      setNewItemName('');
    } catch (err) {
      console.error('Failed to add item:', err);
    }
  };



  const handleSelectList = (listId) => {
    const list = shoppingLists.find((list) => list._id === listId);
    if (list) {
      console.log('Selected list details:', list);
      setSelectedListId(listId);
    } else {
      console.error('No list found with ID:', listId);
    }
  };


  const getChartData = () => {
    if (!selectedListId) return [];
    const selectedList = shoppingLists.find((list) => list.id === selectedListId);
    const totalItems = selectedList?.items.length || 0;
    const resolvedItems = selectedList?.items.filter((item) => item.resolved).length || 0;
    return [
      { name: 'Resolved', value: resolvedItems },
      { name: 'Unresolved', value: totalItems - resolvedItems },
    ];
  };

  const selectedList = shoppingLists.find((list) => list.id === selectedListId);

  if (!isAuthenticated) {
    return (
      <div className="auth-container">
        <h1>{authMode === 'login' ? 'Login' : 'Register'}</h1>
        {authError && <p className="error">{authError}</p>}
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        {authMode === 'register' && (
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        )}
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleAuthSubmit}>
          {authMode === 'login' ? 'Login' : 'Register'}
        </button>
        <p>
          {authMode === 'login' ? (
            <span onClick={() => setAuthMode('register')}>Create an account</span>
          ) : (
            <span onClick={() => setAuthMode('login')}>Already have an account?</span>
          )}
        </p>
      </div>
    );
  }

  return (
    <div className="App">
      <button onClick={handleLogout}>Logout</button>
      <h1>Shopping List</h1>
      <div className="container">
        <div className="sidebar">
          <h2>Shopping Lists</h2>
          <input
            type="text"
            placeholder="New list name"
            value={newListName}
            onChange={(e) => setNewListName(e.target.value)}
          />
          <button onClick={handleAddNewList}>Add New List</button>
          <div className="lists-container">
            {shoppingLists.map((list) => (
              <div
                key={list._id} // Используем уникальный ключ
                className={`list-item ${list._id === selectedListId ? 'selected' : ''}`}
                onClick={() => handleSelectList(list._id)} // Передаем корректный ID
              >
                <span>{list.name}</span>
                {!list.archived && (
                  <button
                    onClick={(e) => {
                      e.stopPropagation(); // Останавливаем событие, чтобы не выбрать список
                      handleArchiveList(list._id);
                    }}
                  >
                    Archive
                  </button>
                )}
                <button
                  onClick={(e) => {
                    e.stopPropagation(); // Останавливаем событие
                    handleDeleteList(list._id);
                  }}
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
          {selectedList && (
            <div>
              <h3>Participants</h3>
              <input
                type="text"
                placeholder="Participant email"
                value={newParticipantEmail}
                onChange={(e) => setNewParticipantEmail(e.target.value)}
              />
              <button onClick={handleAddParticipant}>Add Participant</button>
              <ul>
                {(selectedList.participants || []).map((participant) => (
                  <li key={participant.id || participant._id}>
                    {participant.name || participant.email}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
        <div className="main-content">
          {selectedList ? (
            <>
              <h2>{selectedList.name}</h2>
              <div>
                <h3>Items</h3>
                <input
                  type="text"
                  placeholder="New item"
                  value={newItemName}
                  onChange={(e) => setNewItemName(e.target.value)}
                />
                <button onClick={handleAddItem}>Add Item</button>
                <ul>
                  {(selectedList.items || []).map((item) => (
                    <li key={item.id || item._id}>{item.name}</li>
                  ))}
                </ul>
              </div>
              <div>
                <h3>Overview</h3>
                <PieChart width={300} height={300}>
                  <Pie
                    data={getChartData()}
                    dataKey="value"
                    cx="50%"
                    cy="50%"
                    outerRadius={100}
                  >
                    {getChartData().map((entry, index) => (
                      <Cell
                        key={`cell-${index}`}
                        fill={COLORS[index % COLORS.length]}
                      />
                    ))}
                  </Pie>
                  <Legend />
                </PieChart>
              </div>
            </>
          ) : (
            <p>Select a list to view details</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
