import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { PieChart, Pie, Cell, Legend } from 'recharts';
import './App.css';
import {
  registerUser,
  loginUser,
  fetchShoppingLists,
  createShoppingList,
  deleteShoppingList,
  archiveShoppingList,
  addParticipant,
  addItemToList,
} from './services/apiService'; // Предполагается, что эти функции реализованы в apiService.js
import useFetch from './hooks/useFetch';

const BASE_URL = 'http://localhost:5000/api';

const App = () => {
  const [shoppingLists, setShoppingLists] = useState([]);
  const [newListName, setNewListName] = useState('');
  const [newParticipantName, setNewParticipantName] = useState('');
  const [newItemName, setNewItemName] = useState('');
  const [selectedListId, setSelectedListId] = useState(null);
  const [editingListName, setEditingListName] = useState('');
  const [filter, setFilter] = useState('All');
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [listToDelete, setListToDelete] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(
    !!localStorage.getItem('token')
  );
  const [authMode, setAuthMode] = useState('login');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');
  const [authError, setAuthError] = useState('');
  const COLORS = ['#0088FE', '#FF8042'];

  // Загрузка списка покупок с сервера
  useEffect(() => {
    if (isAuthenticated) {
      fetchShoppingLists()
        .then((lists) => setShoppingLists(lists))
        .catch((err) => console.error('Error fetching shopping lists:', err));
    }
  }, [isAuthenticated]);

  const handleAuthSubmit = async () => {
    try {
      setAuthError('');
      if (authMode === 'register') {
        await registerUser({ email, password, username });
        setAuthMode('login');
      } else {
        const response = await loginUser({ email, password });
        localStorage.setItem('token', response.token);
        setIsAuthenticated(true);
      }
    } catch (err) {
      setAuthError(err.response?.data?.message || 'Authentication failed');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    setEmail('');
    setPassword('');
  };

  const handleAddNewList = async () => {
    if (!newListName.trim()) return;

    try {
      const newList = await createShoppingList(newListName); // вызывает API
      setShoppingLists([...shoppingLists, newList]); // обновляет состояние
      setNewListName('');
      setSelectedListId(newList.id);
      setEditingListName(newList.name);
    } catch (err) {
      console.error('Failed to add new list:', err);
    }
  };


  const handleDeleteList = async (listId) => {
    try {
      await deleteShoppingList(listId);
      setShoppingLists(shoppingLists.filter((list) => list.id !== listId));
      if (listId === selectedListId) {
        setSelectedListId(null);
      }
    } catch (err) {
      console.error('Failed to delete list:', err);
    }
  };

  const handleArchiveList = async (listId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `${BASE_URL}/shopping-lists/${listId}/archive`, // Подключение к вашему бэкенду
        {},
        {
          headers: { Authorization: `Bearer ${token}` }, // Авторизация
        }
      );
      // После успешного архивирования загружаем обновленные данные с сервера
      const updatedLists = await fetchShoppingLists();
      setShoppingLists(updatedLists);
    } catch (err) {
      console.error('Failed to archive list:', err);
    }
  };


  const handleAddParticipant = async () => {
    if (!newParticipantName.trim() || selectedListId === null) return;

    try {
      const updatedList = await addParticipant(selectedListId, newParticipantName);
      setShoppingLists(
        shoppingLists.map((list) =>
          list.id === selectedListId ? { ...list, ...updatedList } : list
        )
      );
      setNewParticipantName(''); // Очистка поля ввода
    } catch (err) {
      console.error('Failed to add participant:', err);
    }
  };



  const handleAddItem = async () => {
    if (!newItemName.trim() || selectedListId === null) return;

    try {
      const updatedList = await addItemToList(selectedListId, newItemName);
      setShoppingLists(
        shoppingLists.map((list) =>
          list.id === selectedListId ? { ...list, ...updatedList } : list
        )
      );
      setNewItemName(''); // Очистка поля ввода
    } catch (err) {
      console.error('Failed to add item:', err);
    }
  };




  const handleSelectList = (listId) => {
    const selectedList = shoppingLists.find((list) => list.id === listId);
    setSelectedListId(listId);
    setEditingListName(selectedList?.name || '');
  };

  const getChartData = () => {
    if (!selectedListId) return [];
    const selectedList = shoppingLists.find((list) => list.id === selectedListId);
    const totalItems = selectedList?.items.length || 0;
    const resolvedItems = selectedList?.items.filter((item) => item.resolved)
      .length;
    return [
      { name: 'Resolved', value: resolvedItems },
      { name: 'Unresolved', value: totalItems - resolvedItems },
    ];
  };

  const selectedList = shoppingLists.find((list) => list.id === selectedListId);

  if (!isAuthenticated) {
    return (
      <div className="auth-container">
        <h1>{authMode === 'login' ? 'Login' : 'Register'}</h1>
        {authError && <p className="error">{authError}</p>}
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        {authMode === 'register' && (
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        )}
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleAuthSubmit}>
          {authMode === 'login' ? 'Login' : 'Register'}
        </button>
        <p>
          {authMode === 'login' ? (
            <span onClick={() => setAuthMode('register')}>Create an account</span>
          ) : (
            <span onClick={() => setAuthMode('login')}>Already have an account?</span>
          )}
        </p>
      </div>
    );
  }

  return (
    <div className="App">
      <button onClick={handleLogout}>Logout</button>
      <h1>Shopping List</h1>
      <div className="container">
        <div className="sidebar">
          <h2>Shopping Lists</h2>
          <input
            type="text"
            placeholder="New list name"
            value={newListName}
            onChange={(e) => setNewListName(e.target.value)}
          />
          <button onClick={handleAddNewList}>Add New List</button>
          <div className="lists-container">
            {shoppingLists.map((list) => (
              <div
                key={list.id}
                className={`list-item ${list.id === selectedListId ? 'selected' : ''}`}
                onClick={() => handleSelectList(list.id)}
              >
                <span>{list.name}</span>
                {list.isArchived ? (
                  <span className="archived-label">Archived</span>
                ) : (
                  <button
                    className="archive-button"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleArchiveList(list.id);
                    }}
                  >
                    Archive
                  </button>
                )}
                <button
                  className="delete-button"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDeleteList(list.id);
                  }}
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
          {selectedList && (
            <div className="participants-section">
              <h3>Participants</h3>
              <input
                type="text"
                placeholder="New participant"
                value={newParticipantName}
                onChange={(e) => setNewParticipantName(e.target.value)}
              />
              <button onClick={handleAddParticipant}>Add Participant</button>
              <ul>
                {selectedList.participants.map((participant) => (
                  <li key={participant.id}>{participant.name}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
        <div className="main-content">
          {selectedList ? (
            <>
              <h2>{selectedList.name}</h2>
              <div>
                <h3>Items</h3>
                {!selectedList.isArchived && (
                  <>
                    <input
                      type="text"
                      placeholder="New item"
                      value={newItemName}
                      onChange={(e) => setNewItemName(e.target.value)}
                    />
                    <button onClick={handleAddItem}>Add Item</button>
                  </>
                )}
                <ul>
                  {selectedList.items.map((item) => (
                    <li key={item.id}>{item.name}</li>
                  ))}
                </ul>
              </div>
              <div>
                <h3>Overview</h3>
                <PieChart width={300} height={300}>
                  <Pie
                    data={getChartData()}
                    dataKey="value"
                    cx="50%"
                    cy="50%"
                    outerRadius={100}
                  >
                    {getChartData().map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Legend />
                </PieChart>
              </div>
            </>
          ) : (
            <p>Select a list to view details</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
