import React, { useState, useEffect } from 'react';
import { PieChart, Pie, Cell, Legend } from 'recharts';
import './App.css';
import {
  registerUser,
  loginUser,
  fetchShoppingLists,
  createShoppingList,
  deleteShoppingList,
  archiveShoppingList,
  unarchiveShoppingList,
  addParticipantByEmail,
  addItemToList,
  toggleItemStatus,
  deleteItem,
} from './services/apiService';

const App = () => {
  const [shoppingLists, setShoppingLists] = useState([]);
  const [newListName, setNewListName] = useState('');
  const [newParticipantEmail, setNewParticipantEmail] = useState('');
  const [newItemName, setNewItemName] = useState('');
  const [selectedListId, setSelectedListId] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));
  const [authMode, setAuthMode] = useState('login');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');
  const [authError, setAuthError] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [language, setLanguage] = useState('en');
  const COLORS = ['#0088FE', '#FF8042'];

  const translations = {
    en: {
      shoppingLists: 'Shopping Lists',
      newListName: 'New list name',
      addNewList: 'Add New List',
      participants: 'Participants',
      participantEmail: 'Participant email',
      addParticipant: 'Add Participant',
      items: 'Items',
      newItem: 'New item',
      addItem: 'Add Item',
      overview: 'Overview',
      logout: 'Logout',
      darkMode: 'Dark Mode',
      lightMode: 'Light Mode',
    },
    ru: {
      shoppingLists: 'Списки покупок',
      newListName: 'Название нового списка',
      addNewList: 'Добавить список',
      participants: 'Участники',
      participantEmail: 'Email участника',
      addParticipant: 'Добавить участника',
      items: 'Предметы',
      newItem: 'Новый предмет',
      addItem: 'Добавить предмет',
      overview: 'Обзор',
      logout: 'Выйти',
      darkMode: 'Тёмная тема',
      lightMode: 'Светлая тема',
    },
    uk: {
      shoppingLists: 'Списки покупок',
      newListName: 'Назва нового списку',
      addNewList: 'Додати список',
      participants: 'Учасники',
      participantEmail: 'Email учасника',
      addParticipant: 'Додати учасника',
      items: 'Речі',
      newItem: 'Нова річ',
      addItem: 'Додати річ',
      overview: 'Огляд',
      logout: 'Вийти',
      darkMode: 'Темна тема',
      lightMode: 'Світла тема',
    },
    cz: {
      shoppingLists: 'Nákupní seznamy',
      newListName: 'Název nového seznamu',
      addNewList: 'Přidat seznam',
      participants: 'Účastníci',
      participantEmail: 'Email účastníka',
      addParticipant: 'Přidat účastníka',
      items: 'Položky',
      newItem: 'Nová položka',
      addItem: 'Přidat položku',
      overview: 'Přehled',
      logout: 'Odhlásit se',
      darkMode: 'Tmavý režim',
      lightMode: 'Světlý režim',
    },
  };

  const t = (key) => translations[language][key];

  useEffect(() => {
    if (isAuthenticated) {
      fetchShoppingLists()
        .then((lists) => setShoppingLists(lists))
        .catch((err) => console.error('Error fetching shopping lists:', err));
    }
  }, [isAuthenticated]);

  const handleAuthSubmit = async () => {
    try {
      setAuthError('');
      if (authMode === 'register') {
        await registerUser({ email, password, username });
        setAuthMode('login');
      } else {
        const response = await loginUser({ email, password });
        localStorage.setItem('token', response.token);
        setIsAuthenticated(true);
      }
    } catch (err) {
      setAuthError(err.response?.data?.message || 'Authentication failed');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    setEmail('');
    setPassword('');
    setMenuOpen(false);
  };

  const handleAddNewList = async () => {
    if (!newListName.trim()) {
      console.error('List name is empty');
      return;
    }

    try {
      const newList = await createShoppingList(newListName);
      setShoppingLists([...shoppingLists, newList]);
      setNewListName('');
      setSelectedListId(newList._id);
    } catch (err) {
      console.error('Failed to add new list:', err);
    }
  };

  const handleDeleteList = async (listId) => {
    try {
      await deleteShoppingList(listId);
      setShoppingLists((prevLists) => prevLists.filter((list) => list._id !== listId));
      if (listId === selectedListId) {
        setSelectedListId(null);
      }
    } catch (err) {
      console.error('Failed to delete list:', err);
    }
  };

  const handleAddParticipant = async () => {
    if (!newParticipantEmail.trim() || !selectedListId) {
      console.error('No email or list selected');
      return;
    }

    try {
      const updatedList = await addParticipantByEmail(selectedListId, newParticipantEmail);
      setShoppingLists((prevLists) =>
        prevLists.map((list) =>
          list._id === selectedListId ? { ...list, participants: updatedList.participants } : list
        )
      );
      setNewParticipantEmail('');
    } catch (err) {
      console.error('Failed to add participant:', err);
    }
  };

  const handleAddItem = async () => {
    if (!newItemName.trim() || !selectedListId) return;
    try {
      const updatedList = await addItemToList(selectedListId, newItemName);
      setShoppingLists((prevLists) =>
        prevLists.map((list) =>
          list._id === selectedListId ? { ...list, items: updatedList.items } : list
        )
      );
      setNewItemName('');
    } catch (err) {
      console.error('Failed to add item:', err);
    }
  };

  const handleToggleItemStatus = async (itemId) => {
    if (!selectedListId) return;

    try {
      const updatedItem = await toggleItemStatus(selectedListId, itemId);
      setShoppingLists((prevLists) =>
        prevLists.map((list) =>
          list._id === selectedListId
            ? {
                ...list,
                items: list.items.map((item) =>
                  item._id === itemId ? { ...item, resolved: updatedItem.item.resolved } : item
                ),
              }
            : list
        )
      );
    } catch (err) {
      console.error('Failed to toggle item status:', err);
    }
  };

  const handleDeleteItem = async (itemId) => {
    if (!selectedListId) return;

    try {
      await deleteItem(selectedListId, itemId);
      setShoppingLists((prevLists) =>
        prevLists.map((list) =>
          list._id === selectedListId
            ? { ...list, items: list.items.filter((item) => item._id !== itemId) }
            : list
        )
      );
    } catch (err) {
      console.error('Failed to delete item:', err);
    }
  };

  const handleSelectList = (listId) => {
    const list = shoppingLists.find((list) => list._id === listId);
    if (list) {
      setSelectedListId(listId);
    } else {
      console.error('No list found with ID:', listId);
    }
  };

  const getChartData = () => {
    const selectedList = shoppingLists.find((list) => list._id === selectedListId);
    if (!selectedList) return [];
    const totalItems = selectedList.items.length;
    const resolvedItems = selectedList.items.filter((item) => item.resolved).length;
    return [
      { name: 'Resolved', value: resolvedItems },
      { name: 'Unresolved', value: totalItems - resolvedItems },
    ];
  };

  const selectedList = shoppingLists.find((list) => list._id === selectedListId);

  if (!isAuthenticated) {
    return (
      <div className="auth-container">
        <h1>{authMode === 'login' ? 'Login' : 'Register'}</h1>
        {authError && <p className="error">{authError}</p>}
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        {authMode === 'register' && (
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        )}
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleAuthSubmit}>
          {authMode === 'login' ? 'Login' : 'Register'}
        </button>
        <p>
          {authMode === 'login' ? (
            <span onClick={() => setAuthMode('register')}>Create an account</span>
          ) : (
            <span onClick={() => setAuthMode('login')}>Already have an account?</span>
          )}
        </p>
      </div>
    );
  }

  return (
    <div className={`App ${darkMode ? 'dark-mode' : ''}`}>
      <div className="settings-icon-container">
        <div className="settings-icon" onClick={() => setMenuOpen(!menuOpen)}>
          ⚙️
        </div>
        {menuOpen && (
          <div className="settings-menu">
            <button onClick={handleLogout}>{t('logout')}</button>
            <button onClick={() => setDarkMode(!darkMode)}>
              {darkMode ? t('lightMode') : t('darkMode')}
            </button>
            <select value={language} onChange={(e) => setLanguage(e.target.value)}>
              <option value="en">English</option>
              <option value="ru">Русский</option>
              <option value="uk">Українська</option>
              <option value="cz">Český</option>
            </select>
          </div>
        )}
      </div>
      <h1>{t('shoppingLists')}</h1>
      <div className="container">
        <div className="sidebar">
          <h2>{t('shoppingLists')}</h2>
          <input
            type="text"
            placeholder={t('newListName')}
            value={newListName}
            onChange={(e) => setNewListName(e.target.value)}
          />
          <button onClick={handleAddNewList}>{t('addNewList')}</button>
          <div className="lists-container">
            {shoppingLists.map((list) => (
              <div
                key={list._id}
                className={`list-item ${list._id === selectedListId ? 'selected' : ''}`}
                onClick={() => !list.archived && handleSelectList(list._id)}
              >
                <span>{list.name}</span>
                {list.archived ? (
                  <button
                    className="unarchive-button"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleUnarchiveList(list._id);
                    }}
                  >
                    Unarchive
                  </button>
                ) : (
                  <button
                    className="archive-button"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleArchiveList(list._id);
                    }}
                  >
                    Archive
                  </button>
                )}
                {!list.archived && (
                  <button
                    className="delete-button"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDeleteList(list._id);
                    }}
                  >
                    Delete
                  </button>
                )}
              </div>
            ))}
          </div>
          {selectedList && (
            <div>
              <h3>{t('participants')}</h3>
              <input
                type="text"
                placeholder={t('participantEmail')}
                value={newParticipantEmail}
                onChange={(e) => setNewParticipantEmail(e.target.value)}
              />
              <button onClick={handleAddParticipant}>{t('addParticipant')}</button>
              <ul>
                {(selectedList.participants || []).map((participant) => (
                  <li key={participant._id || participant.email}>{participant.email}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
        <div className="main-content">
          {selectedList ? (
            selectedList.archived ? (
              <p>{t('archivedListMessage')}</p>
            ) : (
              <>
                <h2>{selectedList.name}</h2>
                <div>
                  <h3>{t('items')}</h3>
                  <input
                    type="text"
                    placeholder={t('newItem')}
                    value={newItemName}
                    onChange={(e) => setNewItemName(e.target.value)}
                  />
                  <button onClick={handleAddItem}>{t('addItem')}</button>
                  <ul>
                    {(selectedList.items || []).map((item) => (
                      <li key={item._id}>
                        <span
                          style={{
                            textDecoration: item.resolved ? 'line-through' : 'none',
                          }}
                        >
                          {item.name}
                        </span>
                        <button onClick={() => handleToggleItemStatus(item._id)}>
                          {item.resolved ? 'Undone' : 'Done'}
                        </button>
                        <button onClick={() => handleDeleteItem(item._id)}>Delete</button>
                      </li>
                    ))}
                  </ul>
                </div>
                <div>
                  <h3>{t('overview')}</h3>
                  <PieChart width={300} height={300}>
                    <Pie
                      data={getChartData()}
                      dataKey="value"
                      cx="50%"
                      cy="50%"
                      outerRadius={100}
                    >
                      {getChartData().map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                      ))}
                    </Pie>
                    <Legend />
                  </PieChart>
                </div>
              </>
            )
          ) : (
            <p>{t('selectListMessage')}</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
