// App.js
import React, { useState } from 'react';
import ShoppingList from './components/ShoppingList';
import ParticipantsList from './components/ParticipantsList';

function App() {
    const [role, setRole] = useState('Участник'); // Начальная роль "Участник"
    const [items, setItems] = useState([]); // Список товаров
    const [participants, setParticipants] = useState(['Участник1', 'Участник2']); // Начальный список участников

    // Функция для добавления нового товара
    const addItem = () => {
        const newItem = prompt("Введите название нового товара:");
        if (newItem) {
            setItems([...items, newItem]); // Добавляем новый товар в массив
        }
    };

    // Функция для переключения роли
    const toggleRole = () => {
        setRole(role === 'Участник' ? 'Владелец' : 'Участник');
    };

    // Функция для добавления нового участника (доступна только владельцу)
    const addParticipant = () => {
        if (role === 'Владелец') {
            const newParticipant = prompt("Введите имя нового участника:");
            if (newParticipant) {
                setParticipants([...participants, newParticipant]);
            }
        }
    };

    // Функция для удаления участника (доступна только владельцу)
    const removeParticipant = (name) => {
        if (role === 'Владелец') {
            setParticipants(participants.filter(participant => participant !== name));
        }
    };

    // Функция для выхода текущего пользователя (Участник может выйти сам)
    const leaveList = () => {
        setParticipants(participants.filter(participant => participant !== 'Участник1')); // например, для текущего участника "Участник1"
    };

    return (
        <div>
            <header style={{ padding: "1rem", backgroundColor: "#333", color: "#fff" }}>
                <h1>Shopping List</h1>
            </header>

            <div style={{ padding: "1rem" }}>
                {/* Кнопка для переключения роли */}
                <button onClick={toggleRole} style={{ marginBottom: "1rem" }}>
                    Переключить роль: {role}
                </button>

                {/* Список участников */}
                <ParticipantsList 
                    participants={participants} 
                    removeParticipant={removeParticipant} 
                    role={role}
                    leaveList={leaveList} 
                />

                {/* Отображение списка товаров */}
                <ShoppingList items={items} />

                {/* Кнопка для добавления товара, видна только если роль - "Владелец" */}
                {role === 'Владелец' && (
                    <button onClick={addItem} style={{ marginTop: "1rem" }}>
                        Добавить товар
                    </button>
                )}

                {/* Кнопка для добавления участника, видна только если роль - "Владелец" */}
                {role === 'Владелец' && (
                    <button onClick={addParticipant} style={{ marginTop: "1rem" }}>
                        Добавить участника
                    </button>
                )}
            </div>
        </div>
    );
}

export default App;
