import React, { useState } from 'react';
import ParticipantsList from './components/ParticipantsList';
// другие импорты

const App = () => {
  // Статусы для участников, владельца и текущего пользователя
  const [participants, setParticipants] = useState([
    { id: 1, name: 'Alex' },
    { id: 2, name: 'Lukas' },
    { id: 3, name: 'Maria' }
  ]);

  const ownerId = 1; // Предполагаем, что Алекс — владелец с id 1
  const currentUserId = 2; // Предполагаем, что текущий пользователь — это участник с id 2

  // Функция для удаления участника
  const handleRemoveParticipant = (participantId) => {
    setParticipants(participants.filter((participant) => participant.id !== participantId));
  };

  // Функция для выхода из списка
  const handleLeaveList = (participantId) => {
    setParticipants(participants.filter((participant) => participant.id !== participantId));
  };

  return (
    <div className="App">
      <h1>Shopping List</h1>
      {/* Передача нужных пропсов в ParticipantsList */}
      <ParticipantsList
        ownerId={ownerId}
        participants={participants}
        currentUserId={currentUserId}
        onRemoveParticipant={handleRemoveParticipant}
        onLeaveList={handleLeaveList}
      />
      {/* остальной код приложения */}
    </div>
  );
};

export default App;
