import React, { useState } from 'react';
import ShoppingListItem from './ShoppingListItem';

function ShoppingList() {
  const [items, setItems] = useState([]);
  const [listName, setListName] = useState('Мой Список');
  const [newItem, setNewItem] = useState('');
  const [filter, setFilter] = useState('all');
  const [members, setMembers] = useState([]);
  const [currentUser, setCurrentUser] = useState('owner'); // "owner" по умолчанию

  const handleAddItem = () => {
    if (newItem.trim()) {
      setItems([...items, { id: Date.now(), name: newItem, resolved: false }]);
      setNewItem('');
    }
  };

  const handleToggleResolved = (id) => {
    setItems(items.map(item => item.id === id ? { ...item, resolved: !item.resolved } : item));
  };

  const handleDeleteItem = (id) => {
    setItems(items.filter(item => item.id !== id));
  };

  const filteredItems = items.filter(item => {
    if (filter === 'all') return true;
    return filter === 'resolved' ? item.resolved : !item.resolved;
  });

  const handleAddMember = (newMember) => {
    if (currentUser === 'owner' && newMember.trim() && !members.includes(newMember)) {
      setMembers([...members, newMember]);
    }
  };

  const handleRemoveMember = (member) => {
    if (currentUser === 'owner') {
      setMembers(members.filter(m => m !== member));
    }
  };

  const handleLeaveList = () => {
    setMembers(members.filter(m => m !== currentUser));
  };

  return (
    <div>
      {/* Название списка доступно только владельцу */}
      {currentUser === 'owner' ? (
        <input
          type="text"
          value={listName}
          onChange={(e) => setListName(e.target.value)}
          placeholder="Название списка"
          style={{ fontSize: '1.5rem', marginBottom: '1rem' }}
        />
      ) : (
        <h2>{listName}</h2>
      )}

      <h2>{listName}</h2>

      {/* Добавление новой позиции */}
      <div>
        <input
          type="text"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          placeholder="Новая позиция"
        />
        <button onClick={handleAddItem}>Добавить</button>
      </div>

      {/* Фильтры */}
      <div>
        <label>
          <input
            type="radio"
            name="filter"
            checked={filter === 'all'}
            onChange={() => setFilter('all')}
          />
          All
        </label>
        <label>
          <input
            type="radio"
            name="filter"
            checked={filter === 'unresolved'}
            onChange={() => setFilter('unresolved')}
          />
          Только невыполненные
        </label>
        <label>
          <input
            type="radio"
            name="filter"
            checked={filter === 'resolved'}
            onChange={() => setFilter('resolved')}
          />
          Только выполненные
        </label>
      </div>

      {/* Список позиций */}
      <ul>
        {filteredItems.map(item => (
          <ShoppingListItem
            key={item.id}
            item={item}
            onToggleResolved={handleToggleResolved}
            onDelete={handleDeleteItem}
          />
        ))}
      </ul>

      {/* Управление участниками для владельца */}
      {currentUser === 'owner' && (
        <div>
          <h3>Управление участниками</h3>
          <input type="text" placeholder="Добавить участника" onKeyDown={(e) => {
            if (e.key === 'Enter') handleAddMember(e.target.value);
          }} />
          <ul>
            {members.map(member => (
              <li key={member}>
                {member} <button onClick={() => handleRemoveMember(member)}>Удалить</button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Кнопка для участника выйти из списка */}
      {currentUser !== 'owner' && (
        <button onClick={handleLeaveList}>Выйти из списка</button>
      )}
    </div>
  );
}

export default ShoppingList;
