import React from 'react';

function ShoppingListItem({ item, onToggleResolved, onDelete }) {
  return (
    <li>
      <span
        style={{ textDecoration: item.resolved ? 'line-through' : 'none' }}
        onClick={() => onToggleResolved(item.id)}
      >
        {item.name}
      </span>
      <button onClick={() => onDelete(item.id)}>Удалить</button>
    </li>
  );
}

export default ShoppingListItem;
