// components/ParticipantsList.js
import React from 'react';

function ParticipantsList({ participants, removeParticipant, role, leaveList }) {
    return (
        <div style={{ padding: "1rem", backgroundColor: "#e0e0e0", borderRadius: "8px", marginBottom: "1rem" }}>
            <h2>Участники</h2>
            <ul>
                {participants.map((participant, index) => (
                    <li key={index} style={{ marginBottom: "0.5rem" }}>
                        {participant}
                        {/* Кнопка удаления участника, видна только для владельца */}
                        {role === 'Владелец' && (
                            <button 
                                onClick={() => removeParticipant(participant)} 
                                style={{ marginLeft: "1rem", color: "red" }}
                            >
                                Удалить
                            </button>
                        )}
                        {/* Кнопка выхода для текущего участника */}
                        {role === 'Участник' && participant === 'Участник1' && (
                            <button 
                                onClick={leaveList} 
                                style={{ marginLeft: "1rem", color: "blue" }}
                            >
                                Выйти
                            </button>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ParticipantsList;
