// src/components/ParticipantsList.js
import React from 'react';

const ParticipantsList = ({ owner, participants, onRemoveParticipant, onLeaveList, isOwner }) => {
  return (
    <div className="participants-list">
      <h3>Participants</h3>

      {/* Отображаем владельца первым в списке */}
      <div className="participant-item owner">
        <span>{owner.name} (Owner)</span>
        {/* Кнопка "Удалить" для владельца отсутствует */}
      </div>

      {/* Отображаем участников с возможностью удаления для владельца и выхода для участников */}
      {participants.map((participant) => (
        <div key={participant.id} className="participant-item">
          <span>{participant.name}</span>

          {/* Условия для показа кнопок:
              - Владелец может удалять других участников
              - Участник может сам выйти из списка */}
          {isOwner ? (
            <button onClick={() => onRemoveParticipant(participant.id)}>Remove</button>
          ) : (
            <button onClick={() => onLeaveList(participant.id)}>Leave</button>
          )}
        </div>
      ))}
    </div>
  );
};

export default ParticipantsList;
