import React from 'react';

function ShoppingListItem({ item, onToggleResolved, onDelete }) {
  return (
    <li style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
      <span
        style={{ textDecoration: item.resolved ? 'line-through' : 'none', cursor: 'pointer' }}
        onClick={() => onToggleResolved(item.id)}
      >
        {item.name}
      </span>
      
      {/* Галочка для выполненных элементов */}
      {item.resolved && <span style={{ color: 'green' }}>✔</span>}
      
      <button onClick={() => onDelete(item.id)}>Удалить</button>
    </li>
  );
}

export default ShoppingListItem;
