import React, { useState } from "react";

function App() {
  const [role, setRole] = useState("member"); // "owner" или "member"

  // Функция для переключения роли
  const toggleRole = () => {
    setRole(role === "owner" ? "member" : "owner");
  };

  return (
    <div className="app">
      <header>
        <h1>Shopping List</h1>
      </header>
      <div className="container">
        <div className="sidebar">
          <h2>My Shopping Lists</h2>
          <button onClick={toggleRole}>
            Переключить роль: {role === "owner" ? "Владелец" : "Участник"}
          </button>
          {/* Здесь компонент списка */}
        </div>
        <div className="main">
          <h2>Список товаров</h2>
          {/* Здесь добавляем список товаров и фильтры */}
        </div>
      </div>
    </div>
  );
}
